import React, { Component } from 'react';
import axios from 'axios';
import { Consumer } from '../../context';




 class Search extends Component {
    state = {
        trackTitle: ''
    };

    findTrack = (e) => {
        e.preventDefault();

        axios
        // https://cors-anywhere.herokuapp.com request call 
        .get(`https://cors-anywhere.herokuapp.com/https://api.musixmatch.com/ws/1.1/
              track.search?q_track=${this.state.trackTitle}&page_size=10&page=1&s_track_rating=desc
              &apikey=${
              process.env.REACT_APP_MM_KEY
            }`
        )

            // response back call
            .then(res => {
                console.log(res.data);
                //this.setState({track_list: res.data.message.body.track_list});
            }) 
            .catch(err => console.log(err));


    }

    onChange = e  => {
        this.setState({ [e.target.name]: e.target.value });
    }

    render() {
        return (
            <Consumer>
                {value => {
                    return (
                        <div className="card card-body mb-4 p-4">
                            <h1 className="display-4 text-center">
                                <i className="fas fa-music"></i> Search For A Song
                            </h1>
                            <p className="lead text-center">Get the lyrics for any song</p>
                            <form onSubmit={this.findTrack}>
                                <div className="form-group">
                                    <input type="text" className="form-control form-control-lg" 
                                    placeholder="Song Title..."
                                    name="trackTitle"
                                    value={this.state.trackTitle}
                                    onChange={this.onChange}
                                    />
                                </div>
                                <button className="btn btn-primary btn-lg btn-block mb-5"
                                type="submit">Get Track Lyrics</button>

                            </form>
                        </div>

                    );
                }}

            </Consumer>
        )
    }
}


export default Search;
