import React, { Component } from 'react';
import axios from 'axios';
import Spinner from '../layout/Spinner';
import {Link} from 'react-router-dom';
import Moment from 'react-moment';

class Lyrics extends Component {


    state = {
        track: {},
        lyrics: {},
    }


    componentDidMount () {
        axios
        // for lyric ~ 
        // https://cors-anywhere.herokuapp.com request call 
        // chart.tracks.get?chart_name=top&page=1&page_size=10&country=us&f_has_lyrics=1  end point 
        // &apikey  API Key
        .get(`https://cors-anywhere.herokuapp.com/https://api.musixmatch.com/ws/1.1/track.lyrics.get?
            track_id=${this.props.match.params.id}&apikey=${
            process.env.REACT_APP_MM_KEY
            }`
        )
            // response back call
            .then(res => {
                this.setState({lyrics: res.data.message.body.lyrics });

                return axios.get(
                    // for track ~ 
                    `https://cors-anywhere.herokuapp.com/https://api.musixmatch.com/ws/1.1/track.get?
                    track_id=${this.props.match.params.id}&apikey=${
                    process.env.REACT_APP_MM_KEY}`
                );
            }) 
            .then(res => {
                this.setState({track: res.data.message.body.track });
            })
            .catch(err => console.log(err)); 

    }

    render() {
        const {track, lyrics } = this.state;

        if( 
            track ===  undefined || 
            lyrics === undefined || 
            Object.key(track).length === 0 || 
            Object.key(lyrics).length === 0
            ) { 
                return <Spinner />
        } else {
            return (
                <React.Fragment>    
                    <Link to="/" className="btn btn-dark btn-sm mb-4">Go Back</Link>
                    <div className="card">
                        <h5 className="card-header">
                            {track.track_name} by {' '}
                            <span className="text-secondary">{track.artist_name}</span>
                        </h5>
                        <div className="card-body">
                            {/* <p className="card-text">{lyrics.lyrics_body}</p> */}
                            {/* format of the lyric outlook */}
                            {lyrics.lyrics_body.split("\n").map(lyric => {
                            return <p className="card-text">{lyric}</p>
                            })}
                        </div>
                    </div>

                    <ul className="list-group mt-3">
                     <li className="list-group-item">
                         <strong>Album ID</strong>: {track.album_id}
                     </li>

                     <li className="list-group-item">
                            <strong>Song Genre</strong>:{' '}
                            {
                                track.primary_genres.music_genre_list.length !== 0
                                ? track.primary_genres.music_genre_list[0].music_genre
                                .music_genre_name: 'N/A'
                            }
                    </li>


                     {/* { <li className="list-group-item">
                         <strong>Song Genre</strong>: {' '}

                      </li> } */}

                         {/*
                            track.primary_genres.music_genre_list.length !== 0
                            ? track.primary.genres.music_genre_list[0].music_genre.music_genre_name:'N/A'
                         */}



                    </ul>
                    <li className="list-group-item">
                        <strong>Explicit Words</strong>: {track.explicit === 0 ? 'No' : 'Yes'}
                    </li>

                    <li className="list-group-item">
                        <strong>Release Date</strong>: <Moment format="MM/DD/YYYY">{track.first_release_date}</Moment>
                    </li>

                </React.Fragment>
            );
        }
    }
}


export default Lyrics;
