import React, { Component } from 'react';
import axios from 'axios';
import Spinner from '../layout/Spinner';

class Lyrics extends Component {
    state = {
        track: {},
        lyrics: {},
    }

    componentDidMount () {
        axios
        // for lyric ~ 
        // https://cors-anywhere.herokuapp.com request call 
        // chart.tracks.get?chart_name=top&page=1&page_size=10&country=us&f_has_lyrics=1  end point 
        // &apikey  API Key
        .get(`https://cors-anywhere.herokuapp.com/https://api.musixmatch.com/ws/1.1/track.lyrics.get?
            track_id=${this.props.match.params.id}&apikey=${
            process.env.REACT_APP_MM_KEY
            }`
        )
            // response back call
            .then(res => {
                this.setState({lyrics: res.data.message.body.lyrics });

                return axios.get(
                    // for track ~ 
                    `https://cors-anywhere.herokuapp.com/https://api.musixmatch.com/ws/1.1/track.get?
                    track_id=${this.props.match.params.id}&apikey=${
                    process.env.REACT_APP_MM_KEY}`
                );
            }) 
            .then(res => {
                this.setState({track: res.data.message.body.track });
            })
            .catch(err => console.log(err)); 

    }

    render() {
        const {track, lyrics } = this.state;
        if( 
            track ===  undefined || 
            lyrics === undefined || 
            Object.keys(track).length === 0 || 
            Object.keys(lyric).length === 0
            ) { 
                return <Spinner />
        } else {
            return <h1>Data returned</h1>;
        }


    }
}


export default Lyrics;
